export default (...loggers) => {
  const n = loggers.length
  const compositeLogger = log => {
    for (let i = 0; i < n; ++i) {
      loggers[i](log)
    }
  }

  for (let i = 0; i < n; ++i) {
    const logger = loggers[i]
    if (logger.logs !== undefined) {
      compositeLogger.logs = logger.logs
      break
    }
  }

  return compositeLogger
}
